class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$(`#post-comment-${e}`).append(o),t.deleteComment($(" .delete-comment-button",o)),$(`#post-${e}-comments-form`)[0].reset(),new ToggleLike($(" .like",o)),new Noty({theme:"relax",text:"Comment Added",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id}">\n        <p>\n            <div class="comments-parent">\n                <small>\n                    <span class="name">${e.user.name}</span>\n\n                    <span class="dateTime">\n                    <a id="like-${e._id}" data-likes="0" class="like" href="/likes/toggle/?id=${e._id}&type=Comment">\n                        <i class="fas fa-thumbs-up"></i> 0 Like\n                    </a> \n                            <a class="delete-comment-button" href="/comments/destroy/${e._id}"><i class="fas fa-trash-alt"></i></a>\n                        \n                            ${e.createdAt.toString().substring(0,24)}\n                    </span>\n                </small>\n                <div> ${e.content} </div>\n            </div>\n        </p>\n    </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}